<?phprequire_once('./../../global.php');$slug = Filter::text($_GET['slug']);	// check project$project = Project::getProjectFromSlug($slug);if($project == null) {	exit();}$relationship = Filter::text($_GET['relationship']);$term = Filter::text($_GET['term']);if($relationship == "unaffiliated") {	$usernames = $project->getUnaffiliatedUsernames($term);} elseif($relationship == 'bannable') {	$usernames = $project->getBannableUsernames($term);} elseif($relationship == 'trusted') {	$usernames = $project->getTrustedUsernames($term);}echo json_encode($usernames);