<?phprequire_once TEMPLATE_PATH.'/site/helper/format.php';$uploads = $SOUP->get('uploads');$id = $SOUP->get('id', 'editUploads');?><script type="text/javascript">$(document).ready(function(){	$('#<?= $id ?> input.delete').click(function(){		var li = $(this).parent();		var id = $(li).attr('id').slice(7); // file-		var deleted = $('<input type="hidden" name="deleted['+id+']" value="'+id+'" />');		$(li).append(deleted);		$(li).fadeOut();	});	});</script><div id="<?= $id ?>"><?phpif(!empty($uploads)) {	echo '<ul class="segmented-list uploads">';	foreach($uploads as $u) {		echo '<li id="delete-'.$u->getID().'">';		echo '<input type="button" class="delete" value="Delete" />';		echo '<a href="'.$u->getDownloadURL().'">'.$u->getOriginalName().'</a> ('.formatFileSize($u->getSize()).')';		echo '</li>';	}	echo '</ul>';} ?></div>