<?php$fork = $SOUP->fork();$fork->set('title', 'New Project');$fork->startBlockSet('body');?><script type="text/javascript">$(document).ready(function(){	$('#txtDeadline').datepicker({			changeMonth: true,			changeYear: true,			dateFormat: 'yy-mm-dd' // MySQL datetime format		});		$('#btnCreateProject').click(function(){		buildPost({			'processPage': '<?= Url::projectNewProcess() ?>',			'info': $('#frmNewItem').serialize(),			'buttonID': 'btnCreateProject'		});	});		$('#txtTitle').focus();});</script><!--p>All you need for a new <?= PIPELINE_NAME ?> project is a title and a pitch (description). The other fields &mdash; specs, rules, and deadline &mdash; are recommended, but optional. We've provided suggestions, but feel free to change or remove them.</p--><!--div class="line"> </div--><form id="frmNewItem"><div class="clear">	<label for="txtTitle">Title<span class="required">*</span></label>	<div class="input">		<input id="txtTitle" name="txtTitle" type="text" maxlength="255" />		<p>The name of your project</p>	</div></div><div class="clear">	<label for="txtPitch">What is your project about?<span class="required">*</span></label>	<div class="input">		<textarea id="txtPitch" name="txtPitch"></textarea>				</div></div><div class="clear">	<label for="txtDeadline">Deadline</label>	<div class="input">		<input id="txtDeadline" name="txtDeadline" type="text" />		<p>When the project should be finished</p>	</div></div><div class="clear">	<label for="chkPrivate">Private</label>	<div class="input">		<input type="checkbox" id="chkPrivate" name="chkPrivate" value="private" />		<p>If checked, this project will be hidden from everyone except members and invitees.</p>	</div></div><div class="clear">	<div class="input">		<input id="btnCreateProject" type="button" value="Create Project" />	</div></div></form><?php$fork->endBlockSet();$fork->render('site/partial/panel');