<?php$ths = $SOUP->get('ths');$data = $SOUP->get('data');$title = $SOUP->get('title', 'Items');$fork = $SOUP->fork();$fork->set('title', $title);$fork->startBlockSet('body');echo '<table class="items">';// table headingsif(!empty($ths)) {	echo '<tr>';	foreach($ths as $th) {		echo '<th>'.$th.'</th>';	}	echo '</tr>';}// table dataif(!empty($data)) {	foreach ($data as $d) {		echo '<tr>';		for($i=0; $i<count($ths); $i++) {			echo '<td>'.$d[$i].'</td>';		}		echo '</tr>';	}} else {	echo '<tr><td>(none)</td></tr>';}echo '</table>';$fork->endBlockSet();$fork->render('site/partial/panel');