<?php$thisUser = User::load(Session::getUserID());$email = $thisUser->getEmail();$fork = $SOUP->fork();$fork->set('id', 'notifications');$fork->set('title', 'Notifications');$fork->startBlockSet('body');function checked($value) {	return (($value == 1) ? 'checked="checked"' : '');}?><script type="text/javascript">$(document).ready(function(){	$('#notifications input:checkbox').change(function(){		var value = ($(this).is(':checked')) ? 'notify' : '';		buildPost({			'processPage': '<?= Url::settingsProcess() ?>',			'info': {				'action': 'notification',				'notificationType': $(this).attr('id'),				'notificationValue': value			}		});	});});</script><p>You will be notified via email (<?= $email ?>) when&hellip;</p><div class="line"></div><ul class="segmented-list notifications">	<li>		<input id="chkMassEmail" type="checkbox" value="notify" <?= checked($thisUser->getNotifyMassEmail()) ?> />		<label for="chkMassEmail">an admin sends a mass email</label>	</li>	<li>		<span class="category">inbox</span>		<input id="chkReceiveMessage" type="checkbox" value="notify" <?= checked($thisUser->getNotifyReceiveMessage()) ?> />		<label for="chkReceiveMessage">someone sends you a message</label>	</li>	<li>		<span class="category">tasks</span>		<input id="chkMakeTaskLeader" type="checkbox" value="notify" <?= checked($thisUser->getNotifyMakeTaskLeader()) ?> />		<label for="chkMakeTaskLeader">someone makes you the leader of a task</label>	</li>	<li>		<span class="category">tasks</span>		<input id="chkCommentTaskLeading" type="checkbox" value="notify" <?= checked($thisUser->getNotifyCommentTaskLeading()) ?> />		<label for="chkCommentTaskLeading">someone comments on a task you are leading</label>	</li>	<li>		<span class="category">tasks</span>		<input id="chkEditTaskJoined" type="checkbox" value="notify" <?= checked($thisUser->getNotifyEditTaskAccepted()) ?> />		<label for="chkEditTaskJoined">someone edits a task you joined</label>	</li>	<li>		<span class="category">tasks</span>		<input id="chkCommentTaskJoined" type="checkbox" value="notify" <?= checked($thisUser->getNotifyCommentTaskAccepted()) ?> />		<label for="chkCommentTaskJoined">someone comments on a task you joined</label>	</li>	<li>		<span class="category">tasks</span>		<input id="chkCommentTaskUpdate" type="checkbox" value="notify" <?= checked($thisUser->getNotifyCommentTaskUpdate()) ?> />		<label for="chkCommentTaskUpdate">someone comments on a contribution you created</label>	</li>		<li>		<span class="category">people</span>		<input id="chkInviteProject" type="checkbox" value="notify" <?= checked($thisUser->getNotifyInviteProject()) ?> />		<label for="chkInviteProject">you are invited to join to a project</label>	</li>		<li>		<span class="category">people</span>		<input id="chkTrustProject" type="checkbox" value="notify" <?= checked($thisUser->getNotifyTrustProject()) ?> />		<label for="chkTrustProject">you are trusted/untrusted in a project</label>	</li>	<li>		<span class="category">people</span>		<input id="chkBannedProject" type="checkbox" value="notify" <?= checked($thisUser->getNotifyBannedProject()) ?> />		<label for="chkBannedProject">you are banned/unbanned from a project</label>	</li>	<li>		<span class="category">discussions</span>		<input id="chkDiscussionStarted" type="checkbox" value="notify" <?= checked($thisUser->getNotifyDiscussionStarted()) ?> />		<label for="chkDiscussionStarted">someone replies to a discussion you started</label>	</li>		<li>		<span class="category">discussions</span>		<input id="chkDiscussionReply" type="checkbox" value="notify" <?= checked($thisUser->getNotifyDiscussionReply()) ?> />		<label for="chkDiscussionReply">someone replies to a discussion you replied to</label>	</li>	</ul><?php$fork->endBlockSet();$fork->render('site/partial/panel');